    ObjectID = require('mongodb').ObjectID,
    mongoose = require('mongoose');
/**
 * 创建中间件以及一些中间件需要用到的插件
 */
var app = express();

app.use(bodyParer.urlencoded({extended:true}));
app.use(cookieParser())
app.use(session({
    resave:false,
    saveUninitialized:false,
    secret: 'keyboard cat'
}))

/**
